namespace Mirasvit\Credit\Observer;

class QuoteCollectTotalsBefore extends AbstractObserver
{
    /**
     * @param \Magento\Framework\Event\Observer $observer
     * @return void
     */
    public function execute(\Magento\Framework\Event\Observer $observer)
    {
        /** @var \Magento\Quote\Model\Quote $quote */
        $quote = $observer->getEvent()->getQuote();

        $quote->setCreditCollected(false);
        if ($quote->getId()) {
            $quote->setBaseCreditAmountUsed(0)
                ->setCreditAmountUsed(0)
                ->save();
            $quote->getShippingAddress()->setBaseCreditAmount(0)
                ->setCreditAmount(0)
                ->save();
        }
    }
}
